def game():
    tries = 6
    global lagain
    if mode == "P" :
        while underlines != letters and tries > 0:
            print(f"A palavra tem {len(letters)} letras!")
            print("Palavra:", *underlines)
            print("Tentativas restantes:", tries)
            print("Letras usadas:", *usedL)
            l = input("Digite uma letra: ").upper()
            if l in usedL :
                print("VocÃª jÃ¡ usou essa letra! Tente outra.")
                continue
            else:
                usedL.append(l.upper())
            if l.isalpha():
                if l in letters:
                    for i, letter in enumerate(letters):
                        if l == letter:
                            underlines[i] = l
                else:
                    tries -= 1
            else: 
                print("Letra invÃ¡lida, tente novamente.")
                tries -= 1
                continue
            if tries == 0 :
                print(f"A palavra era: {word}!")
                print("Que pena.. VocÃª perdeu! Tente novamente!")
                print('''Quer tentar novamente?
Se sim, digite 'S'. Se nÃ£o, digite 'N'.''')
                lagain = input().upper()
                if lagain == "N": 
                    exit("Jogo encerrado!")
                elif lagain == "S" :
                    tryagain_pt()         

    elif mode == "E" :
        while underlines != letters and tries > 0:
            print(f"The word has {len(letters)} letters!")
            print("Word:", *underlines)
            print("Remaining attempts:", tries)
            print("Letters used:", *usedL)
            l = input("Type a letter: ").upper()

            if l in usedL :
                print("You already used this letter! Try another one.")
                continue
            else:
                usedL.append(l.upper()) 
            if l.isalpha():
                if l in letters:
                    for i, letter in enumerate(letters):
                        if l == letter:
                            underlines[i] = l
                else:
                    tries -= 1
            else: 
                print("Invalid letter, please try again.")
                continue
            if tries == 0 :
                print(f"The word was: {word}")
                print("Too bad... You lost! Try again!")
                print('''Do you want to try again?
If yes, type 'Y'. If no, type 'N'.''')
                lagain = input().upper()
                if lagain == "N": 
                    exit("Game over!")
                elif lagain == "Y" :
                    tryagain_en()

    if underlines == letters and mode == "P":
        print(f"ParabÃ©ns! VocÃª acertou! A palavra Ã© {word.title()}!")
        time.sleep(0.5)
    elif underlines == letters and mode == "E":
        print(f"Congratulations! You got it right! The word is {word.title()}!")
        time.sleep(0.5)

